import java.util.*;

public class cc_FLOW002 {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int t = in.nextInt();
		while (t > 0) {
			int a = in.nextInt();
			int b = in.nextInt();
			int r;
			r = a % b;
			System.out.println(r);
			t--;
		}
		// TODO Auto-generated method stub
	}
}
